      "cache": [
        "vendor",
        ".bundle"
      ],
      "commands": [
        "bundle install"
      ],
      "env": {
        "BUNDLE_DEPLOYMENT": "false",
        "BUNDLE_PATH": "vendor/bundle"
      },
      "packages": [
        "ruby-3.4",
        "ruby-3.4-dev",
        "ruby3.4-bundler",
        "build-base"
      ]
    },
    "metadata": {
      "build_system": "Bundler",
