      "framework": "Sinatra",
      "language": "Ruby",
      "project_name": "app",
      "reasoning": "Detected from Gemfile in "
    },
    "runtime": {
      "command": [
        "bundle",
        "exec",
        "ruby",
        "/app/app.rb"
      ],
      "copy": [
        {
          "from": ".",
          "to": "/app"
        }
      ],
      "env": {
        "BUNDLE_GEMFILE": "/app/Gemfile",
        "BUNDLE_PATH": "/app/vendor/bundle"
      },
      "health": {
        "endpoint": "/health"
      },
      "packages": [
        "ruby-3.4",
        "ruby3.4-bundler",
        "libgcc",
        "libstdc++"
