    "build": {
      "artifacts": [
        "target/release/hello-world"
      ],
      "cache": [
        "target",
        ".cargo"
      ],
      "commands": [
        "cargo build --release"
      ],
      "env": {
        "CARGO_HOME": ".cargo"
      },
      "packages": [
        "rust-1.92",
        "build-base",
        "openssl-dev",
        "pkgconf"
      ]
    },
    "metadata": {
      "build_system": "Cargo",
      "confidence": 0.949999988079071,
      "framework": "Actix Web",
      "language": "Rust",
      "project_name": "hello-world",
      "reasoning": "Detected from Cargo.toml in "
    },
    "runtime": {
      "command": [
        "/usr/local/bin/hello-world"
      ],
      "copy": [
        {
          "from": "target/release/hello-world",
          "to": "/usr/local/bin/hello-world"
        }
      ],
      "env": {},
      "health": {
        "endpoint": "/health"
      },
      "packages": [
        "glibc",
        "ca-certificates",
        "openssl"
      ],
      "ports": [
        8080
      ]
    },
    "version": "1.0"
  }
]


